import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const YoungerOrder = () => {
  const [orderList, setOrderList] = useState([
    { id: 1, name: '아메리카노', quantity: 1, price: 3000 },
    { id: 2, name: '카페라떼', quantity: 2, price: 3500 }
  ]);

  const handleDelete = (id) => {
    setOrderList(orderList.filter(item => item.id !== id));
  };

  const handleQuantityChange = (id, delta) => {
    setOrderList(orderList.map(item => item.id === id ? { ...item, quantity: item.quantity + delta } : item));
  };

  const totalQuantity = orderList.reduce((acc, item) => acc + item.quantity, 0);
  const totalPrice = orderList.reduce((acc, item) => acc + item.quantity * item.price, 0);

  return (
    <div className="base">
      <nav className="navbar fixed-top bg-body-tertiary">
        <div className="container-fluid">
          <a className="navbar-brand">ABC 카페 키오스크</a>
          <button type="button" className="btn btn-success call-staff" data-bs-toggle="modal" data-bs-target="#callStaff">직원 호출</button>
        </div>
      </nav>

      <nav className="navbar navbar-expand-lg">
        <div className="container-fluid">
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav flex-column">
              {['커피', '논커피', '주스', '디저트', '티'].map((category, index) => (
                <li className="nav-item" key={index}>
                  <button className={`btn btn-${['primary', 'secondary', 'warning', 'danger', 'success'][index]} nav-link`} style={{ fontSize: '48pt' , marginTop:'3%'}}>{category}</button>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </nav>

      <div className="modal fade" id="callStaff" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h1 className="modal-title fs-5" id="exampleModalLabel">직원 호출</h1>
              <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
            </div>
            <div className="modal-body">
              키오스크 사용이 어려우신가요? 직원이 도와드리겠습니다!
            </div>
            <div className="modal-footer">
              <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">닫기</button>
              <button type="button" className="btn btn-primary" data-bs-dismiss="modal">직원 호출</button>
            </div>
          </div>
        </div>
      </div>

      <div className="container" style={{ marginTop: '80px' }}>
        {[...Array(4)].map((_, rowIndex) => (
          <div className="row" style={{ width: '1000px', float: 'left', marginRight: '2%', marginBottom: '2%' }} key={rowIndex}>
            {[...Array(4)].map((_, colIndex) => (
              <div className="col-md-3" key={colIndex}>
                <a href={`카드${rowIndex * 4 + colIndex + 1}의 링크`}>
                  <div className="card">
                    <svg className="bd-placeholder-img card-img-top" width="100%" height="180" xmlns="http://www.w3.org/2000/svg" role="img" aria-label="Placeholder: Image cap" preserveAspectRatio="xMidYMid slice" focusable="false">
                      <rect width="100%" height="100%" fill="#868e96"></rect>
                      <text x="50%" y="50%" fill="#dee2e6" dy=".3em">Image cap</text>
                    </svg>
                    <div className="card-body">
                      <h3 className="card-title">메뉴 이름</h3>
                      <h4 style={{ color: 'red' }}>가격 입력</h4>
                    </div>
                  </div>
                </a>
              </div>
            ))}
          </div>
        ))}
      </div>

      <div className="bd-example m-0 border-0 order-list">
        <table className="table table-striped" style={{ fontSize: 'large' }}>
          <thead>
            <tr>
              <th scope="col" className="menu-column">메뉴</th>
              <th scope="col" className="quantity-column">수량</th>
              <th scope="col" className="price-column">가격</th>
            </tr>
          </thead>
          <tbody>
            {orderList.map(item => (
              <tr id={`row${item.id}`} key={item.id}>
                <td>
                  <div className="d-flex align-items-center">
                    <button className="btn btn-danger me-2" type="button" onClick={() => handleDelete(item.id)}>삭제</button>
                    <span>{item.name}</span>
                  </div>
                </td>
                <td>
                  <div className="input-group">
                    <button className="btn btn-outline-secondary" type="button" onClick={() => handleQuantityChange(item.id, -1)} disabled={item.quantity <= 1}>-</button>
                    <input type="text" className="form-control" value={item.quantity} readOnly />
                    <button className="btn btn-outline-secondary" type="button" onClick={() => handleQuantityChange(item.id, 1)}>+</button>
                  </div>
                </td>
                <td>{item.quantity * item.price}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="card text-bg-primary mb-3 order-price">
        <ul className="list-group list-group-flush" style={{ fontSize: 'x-large' }}>
          <li className="list-group-item d-flex justify-content-between">주문 수량 <span>{totalQuantity} 잔</span></li>
          <li className="list-group-item d-flex justify-content-between">총 가격 <span>{totalPrice} 원</span></li>
        </ul>
      </div>

      <button type="button" className="btn btn-warning" style={{ position: 'absolute', left: '70%', bottom: '0%', width: '20%', height: '15%', fontSize: 'xx-large' }}>주문 확정</button>
      <button type="button" className="btn btn-outline-success" style={{ position: 'absolute', left: '90%', bottom: '0%', width: '10%', height: '15%', fontSize: 'xx-large' }}>주문 취소</button>
    </div>
  );
};

export default YoungerOrder;
