import React, { useState } from 'react';
import './YoungerOrder.css'
import { Row, Col } from 'react-bootstrap'
import Card from 'react-bootstrap/Card';
import Pic from '../assets/images/americano.jpg';
import { useNavigate } from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.min.css';
import AddModal from './addModal.js';

const YoungerOrder = () => {
    const navigate = useNavigate();
    const dataList = [{
        id: 1,
        name: '아메리카노',
        price: 3000
    },
    {
        id: 2,
        name: '카페라떼',
        price: 3500
    },
    {
        id: 3,
        name: '바닐라라떼',
        price: 3700
    }]

    const [orderList, setOrderList] = useState([])
    const [totalPrice, setTotalPrice] = useState(0)
    const [modalShow, setModalShow] = useState(false);
    const [currentOrder, setCurrentOrder] = useState({})

    const handleConfirm = () => {
        navigate("/purchase");
    }

    const handleClear = () => {
        setOrderList([]);
        setTotalPrice(0);
    }

    const handleOpen = (data) => {
        setCurrentOrder(data);
        setModalShow(true);
    }

    const handleAdd = (data) => {
        const idx = orderList.findIndex((order) => order.id === data.id);
        if (idx !== -1) {
            const newOrderList = [...orderList];
            newOrderList[idx].count += 1;
            setOrderList(newOrderList);
        } else {
            setOrderList([...orderList, { ...data, count: 1 }]);
        }
        setTotalPrice(totalPrice + data.price);
    }

    const getOrderCount = () => {
        let count = 0;
        orderList.forEach((order) => {
            count += order.count;
        });
        return count;
    }

    return (
        <div className="base">
            <nav className="navbar fixed-top bg-body-tertiary">
                <div className="container-fluid">
                    <a className="navbar-brand">ABC 카페 키오스크</a>
                    <button type="button" className="btn btn-success call-staff" data-bs-toggle="modal" data-bs-target="#callStaff"> 직원 호출</button>
                </div>
            </nav>

            <nav className="navbar navbar-expand-lg ">
                <div className="container-fluid">
                    <div className="collapse navbar-collapse" id="navbarNav">
                        <ul className="navbar-nav flex-column" style={{position:'relative', top:'100px'}}>
                            <li className="nav-item">
                                <button className="btn btn-primary nav-link" style={{ fontSize: '48pt' , width:'250px', height:'150px'}}>커피</button>
                            </li>
                            <li className="nav-item">
                                <button className="btn btn-secondary nav-link" style={{ fontSize: '48pt' , width:'250px', height:'150px'}}>논커피</button>
                            </li>
                            <li className="nav-item">
                                <button className="btn btn-warning nav-link" style={{ fontSize: '48pt' , width:'250px', height:'150px'}}>주스</button>
                            </li>
                            <li className="nav-item">
                                <button className="btn btn-danger nav-link" style={{ fontSize: '48pt' , width:'250px', height:'150px'}}>디저트</button>
                            </li>
                            <li className="nav-item">
                                <button className="btn btn-success nav-link" style={{ fontSize: '48pt' , width:'250px', height:'150px'}}>티</button>
                            </li>
                        </ul>
                    </div>
                </div>
            </nav>

            <div className="modal fade" id="callStaff" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h1 className="modal-title fs-5" id="exampleModalLabel">직원 호출</h1>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body">
                            키오스크 사용이 어려우신가요? 직원이 도와드리겠습니다!
                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">닫기</button>
                            <button type="button" className="btn btn-primary" data-bs-dismiss="modal" data-bs-target="#calledStaff">직원 호출</button>
                        </div>
                    </div>
                </div>
            </div>

            <div className="container" style={{ marginTop: '80px' }}>
                <Row style={{position:'relative', top:'-800px', width: '1000px', float: 'left', marginRight: '2%', marginBottom: '2%' }}>
                    {dataList.map((item) => (
                        <Col key={item.id} md={3}>
                            <Card onClick={() => handleOpen(item)}>
                                <Card.Img variant="top" src={Pic} />
                                <Card.Body>
                                    <Card.Title>{item.name}</Card.Title>
                                    <Card.Text>
                                        <h4 style={{ color: 'red' }}>{item.price}￦</h4>
                                    </Card.Text>
                                </Card.Body>
                            </Card>
                        </Col>
                    ))}
                </Row>
            </div>

            <div className="bd-example m-0 border-0 order-list">
                <table className="table table-striped" style={{ fontSize: 'large' , position:'absolute', top:'10%',left:'70%' , width:'30%' }}>
                    <thead>
                        <tr>
                            <th scope="col" className="menu-column">메뉴</th>
                            <th scope="col" className="quantity-column">수량</th>
                            <th scope="col" className="price-column">가격</th>
                        </tr>
                    </thead>
                    <tbody>
                        {orderList.map((order, index) => (
                            <tr key={index}>
                                <td>
                                    <div className="d-flex align-items-center">
                                        <button className="btn btn-danger me-2" type="button" >삭제</button> 
                                        <span>{order.name}</span>
                                    </div>
                                </td>
                                <td>
                                    <div className="input-group">
                                        <button className="btn btn-outline-secondary" type="button">-</button>
                                        <input type="text" className="form-control" value={order.count} disabled />
                                        <button className="btn btn-outline-secondary" type="button" >+</button>
                                    </div>
                                </td>
                                <td>{order.price * order.count}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

            <div className="card text-bg-primary mb-3 order-price" style={{position:'relative', top:'-132px'}}>
                <ul className="list-group list-group-flush" style={{ fontSize: 'x-large' }}>
                    <li className="list-group-item d-flex justify-content-between">주문 수량 <span>{getOrderCount()} 잔</span></li>
                    <li className="list-group-item d-flex justify-content-between">총 가격 <span>{totalPrice} 원</span></li>
                </ul>
            </div>

            {/* <button type="button" className="btn btn-warning" style={{ position: 'fix', left: '68.5%', bottom: '-0.8%', width: '20.5%', height: '15%', fontSize: 'xx-large' }} onClick={handleConfirm}>주문 확정</button>
            <button type="button" className="btn btn-outline-success" style={{ position: 'fix', left: '89%', bottom: '-0.8%', width: '10.5%', height: '15%', fontSize: 'xx-large' }} onClick={handleClear}>주문 취소</button> */}

            <button 
                type="button" 
                className="btn btn-warning" 
                style={{ 
                    position: 'absolute', 
                    left: '68.5%', 
                    top:'85%',  // Negative value causing buttons to be off-screen
                    width: '20.5%', 
                    height: '15%', 
                    fontSize: 'xx-large' 
                }} 
                onClick={handleConfirm}
            >
                주문 확정
            </button>
            <button 
                type="button" 
                className="btn btn-outline-success" 
                style={{ 
                    position: 'absolute', 
                    left: '89%', 
                    bottom: '-0.8%',  // Negative value causing buttons to be off-screen
                    width: '10.5%', 
                    height: '15%', 
                    fontSize: 'xx-large' 
                }} 
                onClick={handleClear}
            >
                주문 취소
            </button>

            <script src="https://cdn.jsdelivr.net/npm/@popperjs/core@2.11.6/dist/umd/popper.min.js" integrity="sha384-qbnM1USAXNCKOIXz1zEUn4PNu8vu7RV3ZksrVP5ZwGCtH4PzV+YX6kFJ3Jz0j2cS" crossOrigin="anonymous"></script>
            <script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.3/dist/js/bootstrap.min.js" integrity="sha384-QwRzoWb/tA5TuLFVllZu2HhHrCgq0uwZng1xgQtNclnTDY3KPOzPb2pIVDKv1OtS" crossOrigin="anonymous"></script>
        </div>
    );
};

export default YoungerOrder;
